
const soma = function (numero1, numero2){
    return numero1+numero2;
}
const subtracao = function (numero1, numero2){
    return numero1-numero2;
}

const multiplicacao = function (numero1, numero2){
    return numero1*numero2;
}

const divisao = function (numero1, numero2){
    
    if(numero2 ==0)
        return 0;

    return numero1/numero2;
}

module.exports  = {
    soma,
    subtracao,
    multiplicacao,
    divisao
}