var assert = require('assert');
const calculadora = require("./calculadora")


describe("Operador Soma", function() {
    it("soma dois números", function() {
        assert.equal(calculadora.soma(1,1),2);
    });

    it("soma um número posito com um número negativo", function() {
        assert.equal(calculadora.soma(-123,123123),123000);
    });
});


describe("Operador Subtração", function() {
    it("Subtrair dois números, sendo um deles negativo", function() {
        assert.equal(calculadora.subtracao(155 ,-10),165);
    });

    it("Divide dois números", function() {
        assert.equal(calculadora.subtracao(550,150),400);
    });
});



describe("Operador Multiplicação", function() {
    it("Mulitiplica dois números, sendo um deles zero", function() {
        assert.equal(calculadora.multiplicacao(8,0),0);
    });

    it("Mulitiplica dois números,", function() {
        assert.equal(calculadora.multiplicacao(1239123 ,12313),15257321499);
    });
});



describe("Operador Divisão", function() {
    it("Divide dois números, sendo um deles negativo", function() {
        assert.equal(calculadora.divisao(123 ,-12),-10.25);
    });

    it("Divide dois números, sendo o divisor zero", function() {
        assert.equal(calculadora.divisao(313123  ,0),0);
    });
});
